mapper.select:
select
id, task_name, task_description, create_time, expire_time, is_finish
from db_doit.task


        and id = #{id}


        and task_name = #{taskName}


        and task_description = #{taskDescription}


        and create_time = #{createTime}


        and expire_time = #{expireTime}


        and is_finish = #{isFinish}
